import osfrom common.config import conffrom common.contants import DATA_DIRfrom common.read_json import ReadJsonfrom common.handle_request import HandleRequestTEST_DATA_PLAN = os.path.join(DATA_DIR, "test_fdp_login_cases.json")http = HandleRequest()json_object = ReadJson(TEST_DATA_PLAN)json_datas = json_object.read_json()cases = json_datas["cases"]base_url = cases[0]["url"]method = cases[0]["method"]url = conf.get("project_url", "url")+ base_urlloginName = conf.get("user_data", "loginName")loginPwd = conf.get("user_data", "loginPwd")logJson = cases[0]["json"]class RegisterLoginApi():    def get_token(self):        response = http.send(url=url, method=method, json=logJson)        result = response.json()        return resultclass GetHeaders:    def __init__(self):        self.login = RegisterLoginApi()        self.login_token ='Bearer'+' '+ self.login.get_token()['token']    def get_headers(self):        headers = {            "Content-Type":conf.get("request_headers", "Content-Type"),            "Connection":conf.get("request_headers", "Connection"),            "charset":conf.get("request_headers", "charset"),            "Authorization": self.login_token        }        return headersif __name__ == '__main__':    res = GetHeaders()    res1 = res.get_headers()    print(res1)