import cx_Oraclefrom common.config import confclass HandleDb:    def __init__(self):        # 连接数据库        # self.con = cx_Oracle.connect("apps","apps95","192.168.20.103:1521/sitpdb1")        self.con = cx_Oracle.connect(conf.get("oracle", "user"),                                   conf.get("oracle", "password"),                                   conf.get("oracle", "TNS"))        # 创建一个游标        self.cur = self.con.cursor()    def get_one(self, sql):        """获取查询到的第一条数据"""        self.con.commit()        self.cur.execute(sql)        return self.cur.fetchone()    def get_all(self, sql):        """获取到查询所有结果"""        self.con.commit()        self.cur.execute(sql)        return self.cur.fetchall()    def count(self, sql):        """计数"""        self.con.commit()        res = self.cur.execute(sql)        return res    def del_data(self, sql):        self.cur.execute(sql)        self.con.commit()    def up_data(self, sql):        self.cur.execute(sql)        self.con.commit()    def close(self):        # 关闭游标对象        self.cur.close()        # 断开连接        self.con.close()if __name__ == '__main__':    db = HandleDb()    sql = "SELECT * FROM SYS_LOGININFOR "    res = db.get_one(sql)    print(res)    db.close()