import jsonimport datetimeimport timeimport requestsclass Enterprise_WeChat_notification():    def __init__(self,total, passed, failed, error, skipped):        self.url = r"https://qyapi.weixin.qq.com/cgi-bin/webhook/send?key=f7ed7de7-276f-4397-8d44-7c16fd83d0c0"        self.header = {'Content-Type': 'application/json'}        message = """【FDP_AutoTest_ProJect】测试环境：sit一期本次执行情况如下：总用例数为: <font color=\"info\">{}条</font>通过用例数为: <font color=\"info\">{}条</font>失败用例数为: <font color=\"warning\">{}条</font>错误用例数为: <font color=\"warning\">{}条</font>跳过用例数为: <font color=\"warning\">{}条</font>通过率为: {}%执行时间：{}""".format(total, passed, failed, error, skipped, passed/total*100, datetime.datetime.now())        self.payload = {            "msgtype": "markdown",            "markdown": {                "content": message,        }        }    def send(self):        requests.post(url=self.url, headers=self.header, data=json.dumps(self.payload))