import osimport pytestimport allurefrom common.contants import DATA_DIRfrom common.read_json import ReadJsonfrom common.handle_request import HandleRequestfrom common.handle_oracle import HandleDbfrom common.config import conffrom common.my_log import LoggerTEST_DATA_PLAN = os.path.join(DATA_DIR, "test_fdp_other_b2b133_b2b161.json")logging = Logger(__name__).get_logger()@allure.feature('其他业务接口9_B2B133_B2B161')@allure.testcase(conf.get("project_url", "url"), "测试地址：{}".format(conf.get("project_url", "url")))class TestOtherB2B161:    # 准备    json_object = ReadJson(TEST_DATA_PLAN)    json_datas = json_object.read_json()    cases = json_datas["cases"]    http = HandleRequest()    db = HandleDb()    @classmethod    def setup_class(self):        logging.info('------>执行前置操作,删除已存在的测试数据')        # 依次删除应收收款临时表、明细表中B2B133数据        self.db.del_data("DELETE  FROM XX_AR_RECEIPT_DETAIL_TEMP  WHERE  SOURCE_TRX_ID ='B2B133-23061401'")        self.db.del_data("DELETE  FROM XX_AR_RECEIPT_DETAIL WHERE SOURCE_TRX_ID ='B2B133-23061401'")        # 依次删除跨币种收款临时表、明细表中B2B161数据        self.db.del_data("DELETE FROM FDP_CROSS_CURRENCY_RECEIPT_T WHERE SOURCE_TRX_ID ='B2B161-23061401'")        self.db.del_data("DELETE FROM FDP_CROSS_CURRENCY_RECEIPT_TEMP_T WHERE SOURCE_TRX_ID ='B2B161-23061401'")    @classmethod    def teardown_class(self):        logging.info('------>执行后置操作,清理测试数据')        # 依次删除应收收款临时表、明细表中B2B133数据        self.db.del_data("DELETE  FROM XX_AR_RECEIPT_DETAIL_TEMP  WHERE  SOURCE_TRX_ID ='B2B133-23061401'")        self.db.del_data("DELETE  FROM XX_AR_RECEIPT_DETAIL WHERE SOURCE_TRX_ID ='B2B133-23061401'")        # 依次删除跨币种收款临时表、明细表中B2B161数据        self.db.del_data("DELETE FROM FDP_CROSS_CURRENCY_RECEIPT_T WHERE SOURCE_TRX_ID ='B2B161-23061401'")        self.db.del_data("DELETE FROM FDP_CROSS_CURRENCY_RECEIPT_TEMP_T WHERE SOURCE_TRX_ID ='B2B161-23061401'")        self.db.close()    @pytest.fixture()    # request.param：用于获取测试的请求参数    def case_data(self, request):        casedata = request.param        return casedata    @allure.title("测试业务类型B2B161是否正确生成数据")    @allure.story('业务类型B2B161测试用例')    @pytest.mark.parametrize('case_data', cases, indirect=True)    def test_other_B2B161(self, case_data,base_headers):        with allure.step("准备B2B161测试数据"):            B2B133_url = conf.get("project_url", "url") + case_data["B2B133_url"]            B2B161_url = conf.get("project_url", "url") + case_data["B2B161_url"]            method = case_data["method"]            # 预期结果            B2B133_expected = case_data["B2B133_expected"]            B2B161_expected = case_data["B2B161_expected"]        # 调用其他业务接口9，生成B2B103数据        with allure.step("执行接口请求,生成B2B133数据"):            response = self.http.send(url=B2B133_url, method=method, json=case_data["B2B133_json"],headers=base_headers)            B2B133_result = response.json()            # result = response.text        with allure.step("执行接口请求,生成B2B161数据"):            response = self.http.send(url=B2B161_url, method=method, json=case_data["B2B161_json"],headers=base_headers)            B2B161_result = response.json()        with allure.step("进行预期结果断言"):            try:                # 请求体断言                assert B2B133_expected["code"] == B2B133_result["code"]                assert B2B133_expected["msg"] == B2B133_result["msg"]                assert B2B161_expected["code"] == B2B161_result["code"]                assert B2B161_expected["msg"] == B2B161_result["msg"]                logging.info('断言成功,返回信息正确')                # 数据库断言--判断应收收款明细表中,B2B131数据是否准确                if case_data["check_sql_01"]:                    if case_data["case_id"] == 'case_0':                        db_actual_results = list({db_actual_results for db_actual_results in                                                  self.db.get_all(case_data["check_sql_01"])})                        try:                            assert db_actual_results[0][0] == case_data["B2B133_json"][0]["sourceCode"]                            assert db_actual_results[0][1] == case_data["B2B133_json"][0]["sourceTrxId"]                            assert db_actual_results[0][2] == case_data["B2B133_json"][0]["companyCode"]                            assert db_actual_results[0][3] == case_data["B2B133_json"][0]["trxType"]                            assert db_actual_results[0][4] == case_data["B2B133_json"][0]["customerId"]                            assert db_actual_results[0][5] == case_data["B2B133_json"][0]["customerNum"]                            assert db_actual_results[0][6] == case_data["B2B133_json"][0]["customerName"]                            assert db_actual_results[0][7] == int(case_data["B2B133_json"][0]["bankAccountId"])                            assert db_actual_results[0][8] == case_data["B2B133_json"][0]["bankAccountName"]                            assert db_actual_results[0][9] == int(case_data["B2B133_json"][0]["receiptAmount"])                            assert db_actual_results[0][10] == 0                            logging.info('断言成功,数据成功生成至应收收款明细表中')                        except KeyError as e:                            logging.info(f'断言失败,数据生成错误:{e}')                # 数据库断言--判断跨币种收款明细表中,B2B161数据是否准确                if case_data["check_sql_02"]:                    if case_data["case_id"] == 'case_0':                        db_actual_results_02 = list({db_actual_results_02 for db_actual_results_02 in                                                  self.db.get_all(case_data["check_sql_02"])})                        try:                            assert db_actual_results_02[0][0] == case_data["B2B161_json"][0]["sourceCode"]                            assert db_actual_results_02[0][1] == case_data["B2B161_json"][0]["sourceTrxId"]                            assert db_actual_results_02[0][2] == case_data["B2B161_json"][0]["sourceDocNumber"]                            assert db_actual_results_02[0][3] == case_data["B2B161_json"][0]["receiptSourceTrxId"]                            assert db_actual_results_02[0][4] == None                            assert db_actual_results_02[0][5] == case_data["B2B161_json"][0]["companyCode"]                            assert db_actual_results_02[0][6] == case_data["B2B161_json"][0]["country"]                            assert db_actual_results_02[0][7] == case_data["B2B161_json"][0]["trxTypeCode"]                            assert db_actual_results_02[0][8] == case_data["B2B161_json"][0]["customerId"]                            assert db_actual_results_02[0][9] == case_data["B2B161_json"][0]["exchangeRate"]                            assert db_actual_results_02[0][10] == case_data["B2B161_json"][0]["receiptAccountName"]                            assert db_actual_results_02[0][11] == case_data["B2B161_json"][0]["receiptAccountId"]                            assert db_actual_results_02[0][12] == int(case_data["B2B161_json"][0]["trxAmount"])                            assert db_actual_results_02[0][13] == case_data["B2B161_json"][0]["currencyCode"]                            logging.info('断言成功,数据未生成至应收事务明细表中')                        except KeyError:                            logging.info('断言失败，测试不通过')            except AssertionError as i:                logging.error("用例{}生成数据{}--执行失败!".format(case_data["title"], case_data["case_id"]))                logging.debug(                    "用例{}生成数据{}--失败请求参数：{}".format(case_data["title"], case_data["case_id"], case_data["B2B161_json"]))                logging.error("用例{}生成数据{}--失败请求返回参数：{}".format(case_data["title"], case_data["case_id"], B2B161_result))                logging.error("用例{}生成数据{}--报错信息：{}".format(case_data["title"], case_data["case_id"], i))                logging.debug("用例{}生成数据{}--预期结果：{}".format(case_data["title"], case_data["case_id"], B2B161_expected))                raise i            else:                logging.info("用例B2B161生成数据：{}-{}--执行通过".format(case_data["title"], case_data["case_id"]))if __name__ == '__main__':    self_test_B2B161 = TestOtherB2B161()    self_test_B2B161.test_other_B2B161()