import osimport pytestimport allurefrom common.contants import DATA_DIRfrom common.read_json import ReadJsonfrom common.handle_request import HandleRequestfrom common.handle_oracle import HandleDbfrom common.config import conffrom common.my_log import LoggerTEST_DATA_PLAN = os.path.join(DATA_DIR, "test_fdp_other_lgs101_lgs121.json")logging = Logger(__name__).get_logger()@allure.feature('其他业务接口9_LGS121')@allure.testcase(conf.get("project_url", "url"), "测试地址：{}".format(conf.get("project_url", "url")))class TestOtherLGS121:    # 准备    json_object = ReadJson(TEST_DATA_PLAN)    json_datas = json_object.read_json()    cases = json_datas["cases"]    http = HandleRequest()    db = HandleDb()    @classmethod    def setup_class(self):        logging.info('------>执行前置操作,删除已存在的测试数据')        # 依次删除应收收款临时表、明细表中LGS101数据        self.db.del_data("DELETE  FROM XX_AR_RECEIPT_DETAIL_TEMP  WHERE  SOURCE_TRX_ID ='LGS101_23062101'")        self.db.del_data("DELETE  FROM XX_AR_RECEIPT_DETAIL WHERE SOURCE_TRX_ID ='LGS101_23062101'")        # 依次删除跨币种收款临时表、明细表中LGS121数据        self.db.del_data("DELETE FROM FDP_CROSS_CURRENCY_RECEIPT_T WHERE SOURCE_TRX_ID ='LGS121_062101'")        self.db.del_data("DELETE FROM FDP_CROSS_CURRENCY_RECEIPT_TEMP_T WHERE SOURCE_TRX_ID ='LGS121_062101'")    @classmethod    def teardown_class(self):        logging.info('------>执行后置操作,清理测试数据')        # 依次删除应收收款临时表、明细表中LGS101数据        self.db.del_data("DELETE  FROM XX_AR_RECEIPT_DETAIL_TEMP  WHERE  SOURCE_TRX_ID ='LGS101_23062101'")        self.db.del_data("DELETE  FROM XX_AR_RECEIPT_DETAIL WHERE SOURCE_TRX_ID ='LGS101_23062101'")        # 依次删除跨币种收款临时表、明细表中LGS121数据        self.db.del_data("DELETE FROM FDP_CROSS_CURRENCY_RECEIPT_T WHERE SOURCE_TRX_ID ='LGS121_062101'")        self.db.del_data("DELETE FROM FDP_CROSS_CURRENCY_RECEIPT_TEMP_T WHERE SOURCE_TRX_ID ='LGS121_062101'")        self.db.close()    @pytest.fixture()    # request.param：用于获取测试的请求参数    def case_data(self, request):        casedata = request.param        return casedata    @allure.title("测试业务类型LGS121是否正确生成数据")    @allure.story('业务类型LGS121测试用例')    @pytest.mark.parametrize('case_data', cases, indirect=True)    def test_other_LGS121(self, case_data,base_headers):        with allure.step("准备LGS101、LGS121测试数据"):            LGS101_url = conf.get("project_url", "url") + case_data["LGS101_url"]            LGS121_url = conf.get("project_url", "url") + case_data["LGS121_url"]            method = case_data["method"]            # 预期结果            LGS101_expected = case_data["LGS101_expected"]            LGS121_expected = case_data["LGS121_expected"]        # 调用其他业务接口9，生成LGS121数据        with allure.step("执行接口请求,生成LGS101数据"):            response = self.http.send(url=LGS101_url, method=method, json=case_data["LGS101_json"],headers=base_headers)            LGS101_result = response.json()            # result = response.text        with allure.step("执行接口请求,生成LGS121数据"):            response = self.http.send(url=LGS121_url, method=method, json=case_data["LGS121_json"],headers=base_headers)            LGS121_result = response.json()        with allure.step("进行预期结果断言"):            try:                # 请求体断言                assert LGS101_expected["code"] == LGS101_result["code"]                assert LGS101_expected["msg"] == LGS101_result["msg"]                assert LGS121_expected["code"] == LGS121_result["code"]                assert LGS121_expected["msg"] == LGS121_result["msg"]                logging.info('断言成功,返回信息正确')                # 数据库断言--判断应收收款明细表中,B2B131数据是否准确                if case_data["check_sql_01"]:                    if case_data["case_id"] == 'case_0':                        db_actual_results = list({db_actual_results for db_actual_results in                                                  list(self.db.get_all(case_data["check_sql_01"]))})                        try:                            assert db_actual_results[0][0] == case_data["LGS101_json"][0]["sourceCode"]                            assert db_actual_results[0][1] == case_data["LGS101_json"][0]["sourceTrxId"]                            assert db_actual_results[0][2] == case_data["LGS101_json"][0]["companyCode"]                            assert db_actual_results[0][3] == case_data["LGS101_json"][0]["trxType"]                            assert db_actual_results[0][4] == case_data["LGS101_json"][0]["customerId"]                            assert db_actual_results[0][5] == case_data["LGS101_json"][0]["customerNum"]                            assert db_actual_results[0][6] == case_data["LGS101_json"][0]["customerName"]                            assert db_actual_results[0][7] == int(case_data["LGS101_json"][0]["bankAccountId"])                            assert db_actual_results[0][8] == case_data["LGS101_json"][0]["bankAccountName"]                            assert db_actual_results[0][9] == int(case_data["LGS101_json"][0]["receiptAmount"])                            assert db_actual_results[0][10] == 0                            logging.info('断言成功,数据成功生成至应收收款明细表中')                        except KeyError as e:                            logging.info(f'断言失败,数据生成错误:{e}')                # 数据库断言--判断跨币种收款明细表中,LGS121数据是否准确                if case_data["check_sql_02"]:                    if case_data["case_id"] == 'case_0':                        db_actual_results_02 = list({db_actual_results_02 for db_actual_results_02 in                                                  list(self.db.get_all(case_data["check_sql_02"]))})                        print(db_actual_results_02)                        try:                            assert db_actual_results_02[0][0] == case_data["LGS121_json"][0]["sourceCode"]                            assert db_actual_results_02[0][1] == case_data["LGS121_json"][0]["sourceTrxId"]                            assert db_actual_results_02[0][2] == case_data["LGS121_json"][0]["sourceDocNumber"]                            assert db_actual_results_02[0][3] == case_data["LGS121_json"][0]["receiptSourceTrxId"]                            assert db_actual_results_02[0][4] == None                            assert db_actual_results_02[0][5] == case_data["LGS121_json"][0]["companyCode"]                            assert db_actual_results_02[0][6] == case_data["LGS121_json"][0]["country"]                            assert db_actual_results_02[0][7] == case_data["LGS121_json"][0]["trxTypeCode"]                            assert db_actual_results_02[0][8] == case_data["LGS121_json"][0]["customerId"]                            assert db_actual_results_02[0][9] == case_data["LGS121_json"][0]["exchangeRate"]                            assert db_actual_results_02[0][10] == case_data["LGS121_json"][0]["receiptAccountName"]                            assert db_actual_results_02[0][11] == case_data["LGS121_json"][0]["receiptAccountId"]                            assert db_actual_results_02[0][12] == int(case_data["LGS121_json"][0]["trxAmount"])                            assert db_actual_results_02[0][13] == case_data["LGS121_json"][0]["currencyCode"]                            logging.info('断言成功,数据生成至应收事务明细表中')                        except KeyError as e:                            logging.info(f'断言失败，测试不通过:{e}')            except AssertionError as i:                logging.error("用例{}生成数据{}--执行失败!".format(case_data["title"], case_data["case_id"]))                logging.debug(                    "用例{}生成数据{}--失败请求参数：{}".format(case_data["title"], case_data["case_id"], case_data["LGS121_json"]))                logging.error("用例{}生成数据{}--失败请求返回参数：{}".format(case_data["title"], case_data["case_id"], LGS121_result))                logging.error("用例{}生成数据{}--报错信息：{}".format(case_data["title"], case_data["case_id"], i))                logging.debug("用例{}生成数据{}--预期结果：{}".format(case_data["title"], case_data["case_id"], LGS121_expected))                raise i            else:                logging.info("用例LGS121生成数据：{}-{}--执行通过".format(case_data["title"], case_data["case_id"]))if __name__ == '__main__':    self_test_LGS121 = TestOtherLGS121()    self_test_LGS121.test_other_LGS121()