import osimport pytestimport allurefrom common.contants import DATA_DIRfrom common.read_json import ReadJsonfrom common.handle_request import HandleRequestfrom common.handle_oracle import HandleDbfrom common.config import conffrom common.my_log import LoggerTEST_DATA_PLAN = os.path.join(DATA_DIR, "test_fdp_payment_osj105.json")logging = Logger(__name__).get_logger()@allure.feature('应付付款接口_OSJ105')@allure.testcase(conf.get("project_url", "url"), "测试地址：{}".format(conf.get("project_url", "url")))class TestPaymentOSJ105:    # 准备    json_object = ReadJson(TEST_DATA_PLAN)    json_datas = json_object.read_json()    cases = json_datas["cases"]    http = HandleRequest()    db = HandleDb()    @classmethod    def setup_class(self):        logging.info('------>执行前置操作,删除已存在的测试数据')        # 依次删除应付付款原始表、原始表、临时表、正式表中数据        self.db.del_data("DELETE FROM FDP_AP_PAYMENT_HEADER_ORIGIN_TEMP  WHERE SOURCE_TRX_ID = 'OSJ105_2023061201'")        self.db.del_data("DELETE FROM FDP_AP_PAYMENT_HEADER_ORIGIN  WHERE SOURCE_TRX_ID = 'OSJ105_2023061201'")        self.db.del_data("DELETE FROM FDP_AP_PAYMENT_HEADER_TEMP  WHERE SOURCE_TRX_ID = 'OSJ105_2023061201'")        self.db.del_data("DELETE FROM FDP_AP_PAYMENT_HEADER_T WHERE SOURCE_TRX_ID = 'OSJ105_2023061201'")    @classmethod    def teardown_class(self):        logging.info('------>执行后置操作,清理测试数据')        # 依次删除应付付款原始表、原始表、临时表、正式表中数据        self.db.del_data("DELETE FROM FDP_AP_PAYMENT_HEADER_ORIGIN_TEMP  WHERE SOURCE_TRX_ID = 'OSJ105_2023061201'")        self.db.del_data("DELETE FROM FDP_AP_PAYMENT_HEADER_ORIGIN  WHERE SOURCE_TRX_ID = 'OSJ105_2023061201'")        self.db.del_data("DELETE FROM FDP_AP_PAYMENT_HEADER_TEMP  WHERE SOURCE_TRX_ID = 'OSJ105_2023061201'")        self.db.del_data("DELETE FROM FDP_AP_PAYMENT_HEADER_T WHERE SOURCE_TRX_ID = 'OSJ105_2023061201'")        self.db.close()    @pytest.fixture()    # request.param：用于获取测试的请求参数    def case_data(self, request):        casedata = request.param        return casedata    @allure.title("测试业务类型OSJ105是否正确生成数据，并且可以成功汇总且推送")    @allure.story('业务类型OSJ105测试用例')    @pytest.mark.parametrize('case_data', cases, indirect=True)    def test_payment_OSJ105(self, case_data,base_headers):        with allure.step("准备OSJ105测试数据"):            url = conf.get("project_url", "url") + case_data["url"]            method = case_data["method"]            # 预期结果            expected = case_data["expected"]        # 调用应付付款接口，生成OSJ105数据        with allure.step("执行接口请求,生成OSJ105数据"):            response = self.http.send(url=url, method=method, json=case_data["json"],headers=base_headers)            result = response.json()            # result = response.text            # print(result)        with allure.step("进行预期结果断言"):            try:                # 请求体断言                assert expected["code"] == result["code"]                assert expected["msg"] == result["msg"]                # 数据库断言--断言数据是否准确生成应付冲减暂估明细正式表                if case_data["check_sql_01"]:                    db_actul_results = list({db_actul_results for db_actul_results in                                                list(self.db.get_all(case_data["check_sql_01"]))})                    print(db_actul_results)                try:                    assert db_actul_results[0][0] == case_data["json"]["apPaymentHeaderReqs"][0]["sourceCode"]                    assert db_actul_results[0][1] == case_data["json"]["apPaymentHeaderReqs"][0]["sourceTrxId"]                    assert db_actul_results[0][2] == case_data["json"]["apPaymentHeaderReqs"][0]["sourceDocNumber"]                    assert db_actul_results[0][4] == case_data["json"]["apPaymentHeaderReqs"][0]["companyCode"]                    assert db_actul_results[0][5] == case_data["json"]["apPaymentHeaderReqs"][0]["trxTypeCode"]                    assert db_actul_results[0][6] == case_data["json"]["apPaymentHeaderReqs"][0]["prepayFlag"]                    assert db_actul_results[0][8] == case_data["json"]["apPaymentHeaderReqs"][0]["vendorNum"]                    assert db_actul_results[0][9] == case_data["json"]["apPaymentHeaderReqs"][0]["vendorName"]                    assert db_actul_results[0][10] == case_data["json"]["apPaymentHeaderReqs"][0]["bankAccountName"]                    assert db_actul_results[0][11] == case_data["json"]["apPaymentHeaderReqs"][0]["bankAccountId"]                    assert db_actul_results[0][13] == int(case_data["json"]["apPaymentHeaderReqs"][0]["paymentAmount"])                    assert db_actul_results[0][14] == case_data["json"]["apPaymentHeaderReqs"][0]["paymentMethod"]                    assert db_actul_results[0][15] == case_data["json"]["apPaymentHeaderReqs"][0]["paymentNumber"]                    assert db_actul_results[0][17] == int(case_data["json"]["apPaymentHeaderReqs"][0]["attachmentNum"])                    assert db_actul_results[0][21] == case_data["json"]["apPaymentHeaderReqs"][0]["currencyCode"]                    assert db_actul_results[0][28] == '0'                    logging.info('数据成功生成至应付付款正式表中,并且状态为0,数据为有效数据')                except KeyError as e:                    logging.info(f'数据未准确生成至应付付款正式表中：{e}')            except AssertionError as i:                logging.error("用例{}生成数据{}--执行失败!".format(case_data["title"], case_data["case_id"]))                logging.debug(                    "用例{}生成数据{}--失败请求参数：{}".format(case_data["title"], case_data["case_id"], case_data["json"]))                logging.error("用例{}生成数据{}--失败请求返回参数：{}".format(case_data["title"], case_data["case_id"], result))                logging.error("用例{}生成数据{}--报错信息：{}".format(case_data["title"], case_data["case_id"], i))                logging.debug("用例{}生成数据{}--预期结果：{}".format(case_data["title"], case_data["case_id"], expected))                raise i            else:                logging.info("用例OSJ105生成数据：{}-{}--执行通过".format(case_data["title"], case_data["case_id"]))if __name__ == '__main__':    self_test_OSJ105 = TestPaymentOSJ105()    self_test_OSJ105.test_payment_OSJ105()